function integer logBase_~SYM[0];
  input integer base, value;
  begin
    value = value-1;
    for (logBase_~SYM[0]=0; value>0; logBase_~SYM[0]=logBase_~SYM[0]+1)
      value = value / base;
  end
endfunction

assign ~RESULT = logBase_~SYM[0](~LIT[0],~LIT[1]);
